export function getHeigtdataFromImage(t,e,a,r=3){let n=document.createElement("canvas");n.width=e,n.height=a;let o=n.getContext("2d"),g=e*a,l=new Float32Array(g);o.drawImage(t,0,0);for(let t=0;t<g;t++)l[t]=0;let d=o.getImageData(0,0,e,a).data,i=0;for(let t=0,e=d.length;t<e;t+=4){let e=d[t]+d[t+1]+d[t+2];l[i++]=e/r}return l}export function toDegrees(t){return t*(180/Math.PI)}export function toRadians(t){return t*(Math.PI/180)}